import { FC } from 'react';
import Animated, { useAnimatedStyle } from 'react-native-reanimated';
import { N, SQUARE_SIZE } from '../../constants';

type Props = {
  index: number;
  progress: Animated.SharedValue<number>;
};

const Square: FC<Props> = ({ index, progress }) => {
  const offsetAngle = (2 * Math.PI) / N;
  const finalAngle = offsetAngle * (N - 1 - index);

  const rStyle = useAnimatedStyle(() => {
    const rotate = Math.min(finalAngle, progress.value);
    return {
      transform: [
        { rotate: `${rotate}rad` },
        {
          translateY: -index * SQUARE_SIZE,
        },
      ],
    };
  });
  return (
    <Animated.View
      style={[
        {
          backgroundColor: 'white',
          height: SQUARE_SIZE,
          aspectRatio: 1,
          opacity: (index + 1) / N,
          position: 'absolute',
        },
        rStyle,
      ]}
    />
  );
};

export default Square;
